mapper.select:
SELECT DISTINCT p.post_id, p.user_id, p.category_id, p.title, p.content, p.contact_info, p.deadline, p.price, p.item_info, p.start_point, p.end_point, p.image1, p.image2, p.image3, p.status, p.view_count, p.like_count, p.comment_count, p.favorite_count, p.trending_level, p.review_time, p.create_time, p.update_time, u.username, u.avatar, c.name as category_name FROM post p LEFT JOIN user u ON p.user_id = u.user_id LEFT JOIN category c ON p.category_id = c.category_id
LEFT JOIN post_tag pt ON p.post_id = pt.post_id
AND p.status = 1
AND p.category_id = #{categoryId}
AND pt.tag_id = #{tagId}
ORDER BY p.trending_level DESC, p.like_count DESC, p.comment_count DESC, p.create_time DESC
ORDER BY p.comment_count DESC, p.create_time DESC
ORDER BY p.create_time DESC
LIMIT #{start}, #{size}

SELECT COUNT(DISTINCT p.post_id)
FROM post p

    LEFT JOIN post_tag pt ON p.post_id = pt.post_id


    AND p.status = 1

        AND p.category_id = #{categoryId}


        AND pt.tag_id = #{tagId}

mapper.update:
UPDATE post
SET view_count = view_count + 1
WHERE post_id = #{postId}

UPDATE post
SET comment_count = comment_count + 1
WHERE post_id = #{postId}

mapper.select:
SELECT interaction_id
FROM interaction
WHERE post_id = #{postId}
AND user_id = #{userId}
AND interaction_type = 'like'
LIMIT 1

mapper.update:
UPDATE interaction
SET status = 1
WHERE post_id = #{postId}
AND user_id = #{userId}
AND interaction_type = 'like'

UPDATE interaction
SET status = 0
WHERE post_id = #{postId}
AND user_id = #{userId}
AND interaction_type = 'like'

UPDATE post
SET like_count = like_count + #{increment}
WHERE post_id = #{postId}

UPDATE post
SET favorite_count = favorite_count + #{increment}
WHERE post_id = #{postId}

mapper.select:
SELECT interaction_id
FROM interaction
WHERE post_id = #{postId}
AND user_id = #{userId}
AND interaction_type = 'favorite'
LIMIT 1

mapper.update:
