mapper.select:
SELECT p.post_id, p.user_id, p.category_id, p.title, p.content, p.image1, p.image2, p.image3, p.status, p.view_count, p.like_count, p.comment_count, p.create_time, u.username, u.avatar, c.name as category_name FROM post p LEFT JOIN user u ON p.user_id = u.user_id LEFT JOIN category c ON p.category_id = c.category_id
AND p.status = 1 AND ( p.title LIKE CONCAT('%', #{keyword}, '%') OR p.content LIKE CONCAT('%', #{keyword}, '%') OR u.username LIKE CONCAT('%', #{keyword}, '%') )
AND p.category_id = #{categoryId}
ORDER BY p.create_time DESC LIMIT #{start}, #{size}

SELECT COUNT(*) FROM post p LEFT JOIN user u ON p.user_id = u.user_id
AND p.status = 1 AND ( p.title LIKE CONCAT('%', #{keyword}, '%') OR p.content LIKE CONCAT('%', #{keyword}, '%') OR u.username LIKE CONCAT('%', #{keyword}, '%') )
AND p.category_id = #{categoryId}

SELECT user_id, username, avatar, signature FROM user
AND status = 0 AND ( username LIKE CONCAT('%', #{keyword}, '%') OR real_name LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY create_time DESC LIMIT #{start}, #{size}
