mapper.select:
SELECT p.post_id, p.user_id, p.category_id, p.title, p.content, p.start_point, p.end_point, p.price, p.item_info, p.contact_info, p.status, p.create_time, p.image1, p.image2, p.image3, u.username, u.avatar, c.name as category_name FROM post p LEFT JOIN user u ON p.user_id = u.user_id LEFT JOIN category c ON p.category_id = c.category_id WHERE p.category_id = 2
AND p.status IN
#{status}
AND p.status IN (0, 1)
ORDER BY p.create_time DESC LIMIT #{start}, #{size}

mapper.update:
UPDATE post
SET view_count = view_count + 1
WHERE post_id = #{postId}
